  `keywords` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '关键字',
  `description` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '描述',
  `template` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '栏目页模板',
  `content_template` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '内容页模板',
  `mobile_template` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '手机栏目页模板',
  `mobile_content_template` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '手机内容页模板',
  `is_blank` tinyint NOT NULL DEFAULT '0' COMMENT '是否新窗口打开:0否1是',
  `order_factor` int unsigned NOT NULL DEFAULT '0' COMMENT '排序字段',
  `parent_id` int unsigned DEFAULT NULL,
  `left` int unsigned DEFAULT NULL,
  `right` int DEFAULT NULL,
  `depth` int unsigned DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `cms_channels_parent_id_index` (`parent_id`),
  KEY `cms_channels_left_index` (`left`),
  KEY `cms_channels_right_index` (`right`),
  KEY `cms_channels_depth_index` (`depth`),
  CONSTRAINT `cms_channels_parent_id_foreign` FOREIGN KEY (`parent_id`) REFERENCES `cms_channels` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
