  `status` tinyint NULL DEFAULT 0 COMMENT '0草稿1发布4下架',
  `applicable` tinyint NULL DEFAULT 1 COMMENT '0所有人1新用户',
  `stock` int NULL DEFAULT -1 COMMENT '-1不限量',
  `tuan_size` int NULL DEFAULT 0 COMMENT 'N人团',
  `order_factor` int NULL DEFAULT 0 COMMENT '越大越靠前',
  `start_time` timestamp(0) NULL DEFAULT NULL,
  `end_time` timestamp(0) NULL DEFAULT NULL,
  `created_at` timestamp(0) NULL DEFAULT NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  `deleted_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 82 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

