-- ----------------------------
-- Table structure for user_money
-- ----------------------------
DROP TABLE IF EXISTS `user_money`;
CREATE TABLE `user_money`  (
  `id` int UNSIGNED NOT NULL AUTO_INCREMENT,
  `user_id` bigint NULL DEFAULT NULL COMMENT '所属用户',
  `type` tinyint NULL DEFAULT NULL COMMENT '资金类型1购物佣金2提现扣除3系统赠送/奖励',
  `order_status` tinyint NULL DEFAULT -2 COMMENT '-2非佣金 -1 未支付; 0-已支付；1-已成团；2-确认收货；3-审核成功；4-审核失败（不可提现）；5-已经结算；8-非多多进宝商品（无佣金订单）;10-已处罚',
  `amount` decimal(10, 2) NULL DEFAULT 0.00 COMMENT '涉及的金额',
  `contributer_id` bigint NULL DEFAULT 0 COMMENT '0系统 其他:贡献者用户ID',
  `order_sn` char(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '订单编号',
  `plantform` tinyint NULL DEFAULT 0 COMMENT '0默认1拼多多2京东3蘑菇街',
  `level` tinyint NULL DEFAULT 0 COMMENT '0默认1直接购物2下级3间接下级',
  `order_pay_time` timestamp(0) NULL DEFAULT NULL COMMENT '订单支付时间',
  `created_at` timestamp(0) NULL DEFAULT NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 77 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of user_money
-- ----------------------------

